<script>
    const handleMode = ()=> {
        const html = document.getElementById('html')
        const buttonLight = document.getElementById('button-light')
        const buttonDark = document.getElementById('button-dark')
        html.classList.toggle('dark')
        if (html.classList.contains('dark')) {
            buttonLight.classList.add('hidden')
            buttonDark.classList.remove('hidden')
        } else {
            buttonLight.classList.remove('hidden')
            buttonDark.classList.add('hidden')
        }
    }
</script>

